module thermostat_testbench;

    // Inputs
    reg mode;
    reg too_cold;
    reg too_hot;
    reg fan_on;

    // Outputs
    wire heater;
    wire aircon;
    wire fan;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .mode(mode),
        .too_cold(too_cold),
        .too_hot(too_hot),
        .fan_on(fan_on),
        .heater(heater),
        .aircon(aircon),
        .fan(fan)
    );

    // Test cases
    integer num_tests_failed = 0;
    task check_output;
        input reg expected_heater;
        input reg expected_aircon;
        input reg expected_fan;
        input integer test_num;
        begin
            #10;
            if (heater !== expected_heater || aircon !== expected_aircon || fan !== expected_fan) begin
                $display("Test case %d failed: Expected outputs: heater=%b, aircon=%b, fan=%b, Got: heater=%b, aircon=%b, fan=%b", test_num, expected_heater, expected_aircon, expected_fan, heater, aircon, fan);
                num_tests_failed = num_tests_failed + 1;
            end
        end
    endtask

    initial begin
        // Initialize Inputs
        mode = 0;
        too_cold = 0;
        too_hot = 0;
        fan_on = 0;

        // Test case 1: Cooling mode, it's too hot
        mode = 0; too_cold = 0; too_hot = 1; fan_on = 0;
        check_output(0, 1, 1, 1);

        // Test case 2: Heating mode, it's too cold
        mode = 1; too_cold = 1; too_hot = 0; fan_on = 0;
        check_output(1, 0, 1, 2);

        // Test case 3: Cooling mode, not too hot and fan request on
        mode = 0; too_cold = 0; too_hot = 0; fan_on = 1;
        check_output(0, 0, 1, 3);

        // Test case 4: Heating mode, not too cold with fan manually on
        mode = 1; too_cold = 0; too_hot = 0; fan_on = 1;
        check_output(0, 0, 1, 4);

        // Test case 5: Cooling mode, all signals off
        mode = 0; too_cold = 0; too_hot = 0; fan_on = 0;
        check_output(0, 0, 0, 5);

        // Test case 6: Heating mode, all signals off
        mode = 1; too_cold = 0; too_hot = 0; fan_on = 0;
        check_output(0, 0, 0, 6);

        #10; // Wait for all checks to complete
        if (num_tests_failed == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end

endmodule

