module testbench;

    // DUT Inputs
    reg clk;
    reg areset;
    reg bump_left;
    reg bump_right;
    reg ground;
    reg dig;

    // DUT Outputs
    wire walk_left;
    wire walk_right;
    wire aaah;
    wire digging;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Generate clock
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock period of 10ns
    end

    // Test cases
    integer tests_passed = 0;
    initial begin
        // Initialize Inputs
        areset = 1;  // Assert reset
        bump_left = 0;
        bump_right = 0;
        ground = 1;
        dig = 0;

        // Wait for reset and clock stabilization
        #15;  
        areset = 0;  // Deassert reset

        // Test Case 1: Initially walking left
        #10;
        if (walk_left && !walk_right && !aaah && !digging) tests_passed = tests_passed + 1;

        // Test Case 2: Bump on the left, should walk right
        bump_left = 1;
        #10; bump_left = 0;
        if (walk_right && !walk_left && !aaah && !digging) tests_passed = tests_passed + 1;

        // Test Case 3: Bumped on the right, should walk left
        bump_right = 1;
        #10; bump_right = 0;
        if (walk_left && !walk_right && !aaah && !digging) tests_passed = tests_passed + 1;

        // Test Case 4: Ground disappears while walking left
        ground = 0;
        #10;
        if (!walk_left && !walk_right && aaah && !digging) tests_passed = tests_passed + 1;

        // Test Case 5: Ground appears, should resume walking left
        ground = 1;
        #10;
        if (walk_left && !walk_right && !aaah && !digging) tests_passed = tests_passed + 1;

        // Test Case 6: Dig command while walking
        dig = 1;
        #10; dig = 0;
        if (!walk_left && !walk_right && !aaah && digging) tests_passed = tests_passed + 1;

        // Test Case 7: Stop digging and fall
        ground = 0;
        #10;
        if (!walk_left && !walk_right && aaah && !digging) tests_passed = tests_passed + 1;

        // Check all test cases
        if (tests_passed == 7) begin
            $display("all test cases passed");
        end

        $finish;
    end

endmodule

