module testbench;

  // Testbench signals
  reg clk, reset, in;
  wire disc, flag, err;
  reg [7:0] test_vector[0:99]; // Array to store test vectors
  integer i;

  // Instantiate the DUT
  top_module dut (
    .clk(clk),
    .reset(reset),
    .in(in),
    .disc(disc),
    .flag(flag),
    .err(err)
  );

  // Clock generation
  initial begin
    clk = 0;
    forever #5 clk = !clk; // Generate a clock with 10ns period (100 MHz)
  end

  // Stimulus
  initial begin
    // Initialize inputs and load test vectors
    reset = 1;
    in = 0;

    // Defining test vectors, scenarios including necessary correct and error cases
    test_vector[0] = 8'b00000000; // No flags
    test_vector[1] = 8'b01111110; // Valid flag
    test_vector[2] = 8'b01111100; // Discard after 0111110
    test_vector[3] = 8'b11111111; // Error (7 ones)
    test_vector[4] = 8'b01111111; // Error (7 ones)
    test_vector[5] = 8'b01111011; // No event, regular sequence
    test_vector[6] = 8'b01111110; // Valid flag (repeated to check consistency)
    test_vector[7] = 8'b01111110; // Valid flag after flag

    // Apply Reset
    #10;
    reset = 0;
    #10;
    reset = 1;
    #10;
    reset = 0;

    // Start inputting test vectors
    for(i = 0; i < 8; i=i+1) begin
      #10 in = test_vector[i][7];
         #10 in = test_vector[i][6];
         #10 in = test_vector[i][5];
         #10 in = test_vector[i][4];
         #10 in = test_vector[i][3];
         #10 in = test_vector[i][2];
         #10 in = test_vector[i][1];
         #10 in = test_vector[i][0];
    end

    // Completed applying test vectors, check for pass/fail
    if (disc === 1'b0 && flag === 1'b1 && err === 1'b0)
      $display("all test cases passed");

    // Finish the simulation
    #100 $finish;
  end

endmodule

