    top_module dut (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Clock generation
    always #5 clk = ~clk;

    initial begin
        // Initialize Inputs
        clk = 0;
        areset = 1;
        bump_left = 0;
        bump_right = 0;
        ground = 1;
        dig = 0;

        // Reset the DUT
        #10;
        areset = 0;
        #10;
        areset = 1;
        #10;
        areset = 0;

        // Test sequences 

