        @(posedge clk);
        bump_left = 1;   // Bump left to cause a walk right
        @(posedge clk);
        bump_left = 0;
        #10;
        if (walk_right != 1'b1 || walk_left != 1'b0) 
            $display("Test Case 1 Failed");

        // Test Case 2: Lemming should fall when ground disappears
        @(posedge clk);
        ground = 0;
        #10;
        if (aaah != 1'b1) 
