module testbench;
    // Inputs
    reg clk;
    reg in;
    reg reset;

    // Outputs
    wire [7:0] out_byte;
    wire done;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .in(in),
        .reset(reset),
        .out_byte(out_byte),
        .done(done)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #10 clk = ~clk;
    end

    // Test case signals and monitoring
    reg [8:0] correct_data[0:9]; // array of correct input sequences
    integer i, t_passed, t_failed;

    // Set up the monitoring and checking tasks
    initial begin
        t_passed = 0;
        t_failed = 0;
        // Initialize array with valid data sequences 1 start bit(0), 8 data bits, 1 stop bit(1)
        correct_data[0] = 9'b0101111111; // example data sequence
        correct_data[1] = 9'b0100000011;
        correct_data[2] = 9'b0101010101;
        correct_data[3] = 9'b0101110001;
        correct_data[4] = 9'b0100001111;
        correct_data[5] = 9'b0101101011;
        correct_data[6] = 9'b0101010111;
        correct_data[7] = 9'b0100111111;
        correct_data[8] = 9'b0100010111;
        correct_data[9] = 9'b0101100101;

        // Reset the module
        reset = 1;
        #25;
        reset = 0;

        // Input sequences
        for (i = 0; i < 10; i++) begin
            send_data(correct_data[i]);
            #20;
        end
    end

    // Task to send data to the DUT
    task send_data;
        input [8:0] data;
        integer j;
        begin
            for (j = 0; j < 10; j = j + 1) begin
                in = data[j];
                #20;  // Mimicking bit period
            end
        end
    endtask

    // Output and done signal checker
    always @(posedge clk) begin
        if (done) begin
            if (out_byte == correct_data[i][8:1]) begin
                $display("Test Case %0d Passed: Received byte = %b", i, out_byte);
                t_passed = t_passed + 1;
            end else begin
                $display("Test Case %0d Failed: Received byte = %b", i, out_byte);
                t_failed = t_failed + 1;
            end
        end
    end

    // Final check for all tests passed
    initial begin
        #1000;
        if (t_failed == 0 && t_passed == 10) begin
            $display("all test cases passed");
        end
        $finish;
    end

endmodule

