        #10;   // Wait for another clock edge
        if (Q !== Q) begin
            $display("Test case 1 failed");
            $finish;
        end
        test_count = test_count + 1;

        // Test case 2: j=0, k=1, Q should be 0
        j = 0; k = 1;
        #10;
        if (Q !== 0) begin
            $display("Test case 2 failed");
            $finish;
        end
        test_count = test_count + 1;

        // Test case 3: j=1, k=0, Q should be 1
        j = 1; k = 0;
        #10;
        if (Q !== 1) begin
            $display("Test case 3 failed");
            $finish;
        end
        test_count = test_count + 1;

        // Test case 4: j=1, k=1, Q should toggle
        Q_prev = Q;
        j = 1; k = 1;
        #10;
        if (Q === Q_prev) begin
            $display("Test case 4 failed");
            $finish;
        end
        test_count = test_count + 1;

        // If all test cases passed
        if (test_count == 4) begin
            $display("all test cases passed");
        end

        $finish;    
    end

endmodule

