    reg [254:0] in;
    wire [7:0] out;
    integer i;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .in(in),
        .out(out)
    );

    initial begin
        // Initialize Inputs
        in = 0;

        // Apply test cases
        // Test Case 1: All zeros - expected output 0
        in = 255'h0;
        #10;
        if (out !== 8'd0) begin
            $display("Test case failed (All zeros)");
            $finish;
        end

        // Test Case 2: All ones - expected output 255
        in = {255{1'b1}};
        #10;
        if (out !== 8'd255) begin
            $display("Test case failed (All ones)");
            $finish;
        end

        // Test Case 3: Alternate ones - expected output 128
        in = 255'hAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA;
        #10;
        if (out !== 8'd128) begin
            $display("Test case failed (Alternate ones)");
            $finish;
        end

        // Test Case 4: Single one at different positions
        for (i = 0; i < 255; i=i+1) begin
            in = (1 << i);
            #10;
            if (out !== 8'd1) begin
                $display("Test case failed (Single one at position %d)", i);
                $finish;
            end
        end

        // Randomized tests
        repeat (20) begin
            in = $random;
            #10;
            if (out !== pop_count(in)) begin
                $display("Random test case failed");
                $finish;
            end
        end

        $display("all test cases passed");
        $finish;
    end

    // Function to calculate population count
    function [7:0] pop_count(input [254:0] data);
        integer j;
        pop_count = 0;
        for (j = 0; j < 255; j=j+1) begin
            pop_count = pop_count + data[j];
        end
    endfunction
