        run_test(0, 0, 1, 0, 1);
        run_test(0, 1, 0, 0, 1);
        run_test(0, 1, 1, 1, 0);
        run_test(1, 0, 0, 0, 1);
        run_test(1, 0, 1, 1, 0);
        run_test(1, 1, 0, 1, 0);
        run_test(1, 1, 1, 1, 1);

