    wire [7:0] ss;

    // Variables for checking test status
    integer test_cases_failed = 0;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .ena(ena),
        .pm(pm),
        .hh(hh),
        .mm(mm),
        .ss(ss)
    );

    // Clock generator
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Toggle clock every 5ns
    end

    // Test cases
    initial begin
        // Reset the test environment
        reset = 1; ena = 0;
        #20; // Wait for 20 ns
        reset = 0;

        // Case 1: Check reset state, should be 12:00 AM
        #10; // Let reset settle
        if (!(hh == 8'h12 && mm == 8'h00 && ss == 8'h00 && pm == 0)) begin
            test_cases_failed = test_cases_failed + 1;            
        end

        // Case 2: Enable handling and clock ticking: 12:00 AM to 12:00:01 AM
        ena = 1;
        #10;
        if (!(hh == 8'h12 && mm == 8'h00 && ss == 8'h01 && pm == 0)) begin
            test_cases_failed = test_cases_failed + 1;            
        end

        // Case 3: Increment to 12:00:59 AM
        repeat(58) begin
            #10;
        end
        if (!(hh == 8'h12 && mm == 8'h00 && ss == 8'h59 && pm == 0)) begin
            test_cases_failed = test_cases_failed + 1;            
        end

        // Case 4: Roll over to 12:01:00 AM
        #10;
        if (!(hh == 8'h12 && mm == 8'h01 && ss == 8'h00 && pm == 0)) begin
            test_cases_failed = test_cases_failed + 1; 
        end

        // Case 5: Reset during operation
        ena = 1; #5; reset = 1; #10; reset = 0;
        if (!(hh == 8'h12 && mm == 8'h00 && ss == 8'h00 && pm == 0)) begin
            test_cases_failed = test_cases_failed + 1;
        end

        // Add more cases as required...

        // Print Result
        if (test_cases_failed == 0) begin
            $display("all test cases passed");
        end

        $finish;
    end

endmodule

