        .train_history(train_history),
        .train_pc(train_pc)
    );

    initial begin
        $monitor("At time %t, predict_taken: %b, predict_history: %b", 
                 $time, predict_taken, predict_history);
        // Asynchronous reset initial setting
        areset = 1;
        #10;
        areset = 0;
