module testbench;

    reg [3:1] y;
    reg w;
    wire Y2;
    reg expected_Y2;
    integer i, error_counter;

    // Instantiate the DUT
    top_module dut (
        .y(y),
        .w(w),
        .Y2(Y2)
    );

    // Define the array to hold test vectors outside the initial block
    reg [4:0] testvectors[11:0]; // 3 bits for y, 1 bit for w, and 1 for expected Y2

    initial begin
        // Initialize variables
        y = 3'b000; // Start with state 'A' which is 000
        w = 0;
        error_counter = 0;

        // Test vector format: {y, w} -> expected Y2
        // Each vector corresponds to a state and an input w, with expected outputs for Y2

        // Load test vectors
        testvectors[0] = 5'b000_0; // A --0--> B
        testvectors[1] = 5'b000_1; // A --1--> A
        testvectors[2] = 5'b001_0; // B --0--> C
        testvectors[3] = 5'b001_1; // B --1--> D
        testvectors[4] = 5'b010_0; // C --0--> E
        testvectors[5] = 5'b010_1; // C --1--> D
        testvectors[6] = 5'b011_0; // D --0--> F
        testvectors[7] = 5'b011_1; // D --1--> A
        testvectors[8] = 5'b100_0; // E --0--> E
        testvectors[9] = 5'b100_1; // E --1--> D
        testvectors[10] = 5'b101_0; // F --0--> C
        testvectors[11] = 5'b101_1; // F --1--> D

        // Apply test vectors
        for (i = 0; i < 12; i = i + 1) begin
            {y, w} = testvectors[i][4:1];
            expected_Y2 = testvectors[i][0];

            #10; // wait 10ns for the output to stabilize

            // Check the output
            if (Y2 !== expected_Y2) begin
                $display("Error at test vector %d: y=%b, w=%b. Expected Y2=%b, Got Y2=%b", i, y, w, expected_Y2, Y2);
                error_counter = error_counter + 1;
            end
        end

        // Check if all cases passed
        if (error_counter == 0) begin
            $display("all test cases passed");
        end

        // Finish simulation
        $finish;
    end

endmodule

