    top_module DUT (
        .clk(clk),
        .reset(reset),
        .in(in),
        .out(out)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = !clk; // Generate a clock with period 10ns
    end

    // Test cases
    initial begin
        // Initialize inputs
        reset = 1;
        in = 32'b0;
        #10;

        reset = 0;
        // Test Case 1: Check reset functionality
        in = 32'hFFFFFFFF; // All inputs high
        #10;
        reset = 1; // Activate Reset
        #10;
        if (out !== 32'h0) begin
            $display("Test Case 1 Failed");
            $finish;
        end
        reset = 0;

        // Test Case 2: Input 1 to 0 transition, each bit individually
        for (integer i = 0; i < 32; i++) begin
            in = 32'hFFFFFFFF; // Set all high
            #10;
            in[i] = 0; // Create falling edge at bit i
            #10;
            if (out[i] !== 1'b1) begin
                $display("Test Case 2 Failed at bit %d", i);
                $finish;
            end
        end

        // Test Case 3: Ensure output persists until reset
        for (integer i = 0; i < 32; i++) begin
            reset = 1; // Assert reset
            #10;
            reset = 0; // Deassert reset
            in = 32'h0; // Set all low
            #10;
            in = 32'hFFFFFFFF; // Set all high again
            #10;
            if (out[i] !== 1'b0) begin
                $display("Test Case 3 Failed at bit %d", i);
                $finish;
            end
        end

        $display("all test cases passed");
