module testbench;

    reg clk, areset, bump_left, bump_right, ground, dig;
    wire walk_left, walk_right, aaah, digging;

    top_module DUT (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Generate clock
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100 MHz clock
    end

    // Test sequence
    initial begin
        // Initialize inputs
        areset = 1; bump_left = 0; bump_right = 0; ground = 1; dig = 0;
        #10 areset = 0;  // Release reset
        #10 test_walking_behaviors();
        #10 test_falling_splatter();
        #10 test_digging_behavior();
        #50 check_pass_condition();
        #10 $finish;
    end

    task test_walking_behaviors;
        // Test walking and bumping behaviors
        @(posedge clk); ground = 1; bump_right = 1;  // Should begin walking left, bump to right
        @(posedge clk); bump_right = 0;
        @(posedge clk); bump_left = 1;  // Now bump to left
        @(posedge clk); bump_left = 0;
    endtask

    task test_falling_splatter;
        // Test falling and possible splattering
        @(posedge clk); ground = 0;  // Start falling
        repeat(21) @(posedge clk);  // Fall for 21 cycles
        ground = 1;  // Hit the ground; should splatter if falling time > 20 cycles
        @(posedge clk);
    endtask

    task test_digging_behavior;
        // Test digging behavior
        @(posedge clk); ground = 1; dig = 1;  // Start digging
        @(posedge clk); dig = 0; ground = 0;  // Finish digging and start falling
        @(posedge clk); ground = 1;  // Hit ground and continue walking
    endtask

    reg test_passed = 1'b1;

    always @(walk_left, walk_right, aaah, digging) begin
        if ({walk_left, walk_right, aaah, digging} === 4'b0000)
            test_passed = 0; // Detect splatter state
    end

    task check_pass_condition;
        if (test_passed)
            $display("all test cases passed");
        else
            $display("");
    endtask

endmodule

