  initial clk = 0;
  always #1 clk = ~clk;

  // Test vector
  initial begin
    // Initialize Inputs
    reset = 1;   // Apply synchronous reset
    #2 reset = 0;

    // Wait for 1 clock cycle after releasing reset
    #2;

    // Check if the counter is reset to 0
    if (q !== 0) begin
      $display("Test Case Failed: Counter did not reset to 0");
    end

    // Monitor counter value
    for (i = 0; i < 1000; i++) begin
      #2;  // Wait for a clock period
      if (i < 999) begin
        if (q !== i) begin
          $display("Test Case Failed: Counter value error at %d, expected %d, got %d", i, i, q);
        end
      end else begin
        // Check wrapping around behavior
        if (q !== 999) begin
          $display("Test Case Failed: Counter didn't reach 999 at cycle 999");
        end
        #2;  // Move to next cycle which should wrap the counter
        if (q !== 0) begin
          $display("Test Case Failed: Counter did not wrap from 999 to 0");
          finish_test();  // Error, end simulation
        end
      end
    end

    // If we get here, all test cases have passed
    $display("all test cases passed");
    finish_test();
  end

  task finish_test;
  begin
    #5 $finish;
  end
  endtask

