        clk = 0;
        forever #5 clk = ~clk; // Clock with period of 10ns
    end

    // Test cases
    initial begin
        $monitor("Time = %d, clk = %b, load = %b, data = %d, tc = %b", 
                 $time, clk, load, data, tc);

        // Initialize inputs
        load = 0;
        data = 0;

        // Reset conditions
        load = 1; data = 10'd20; // Load the counter with 20
        #10 load = 0; // Start counting down

        // Observe counting
        #200; // Wait for 200ns; counter should have decremented
        if (tc !== 1) begin
            $display("Test Case Failed: The tc did not assert after expected time.");
            $finish;
        end

        // Reload while counting
        #10 load = 1; data = 10'd10; // Load the counter with 10 while still active
        #10 load = 0;
        #100; // Wait for tc to assert again
        if (tc !== 1) begin
            $display("Test Case Failed: tc did not assert after reloading during count.");
            $finish;
        end

        // Check hold at zero
        #10; // Extra time to verify it holds at zero
        if (tc !== 1) begin
            $display("Test Case Failed: tc does not hold at zero.");
            $finish;
        end

        // Check if the tc backs to 0 correctly if reloaded
        #10 load = 1; data = 10'd15;
        #10 load = 0;
        // Wait less than the loaded count value to confirm no premature tc
        #140;
        if (tc !== 0) begin
            $display("Test Case Failed: Premature assert of tc.");
            $finish;
        end

        // Ensure it asserts exactly on the next clock after the last decrement
        #10;
        if (tc !== 1) begin
            $display("Test Case Failed: tc did not assert when expected.");
            $finish;
        end

        #10; // give a little extra time
        $display("all test cases passed");
