module testbench;

    // Inputs
    reg clk;
    reg reset;
    reg in;

    // Outputs
    wire disc;
    wire flag;
    wire err;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .reset(reset),
        .in(in),
        .disc(disc),
        .flag(flag),
        .err(err)
    );

    // Clock generation
    always begin
        clk = 0; #5; clk = 1; #5;
    end

    // Test cases
    integer i;
    reg [7:0] data_stream[0:5];
    reg expected_disc;
    reg expected_flag;
    reg expected_err;
    integer error_count;

    initial begin
        // Initialize Inputs
        clk = 0;
        reset = 1;
        in = 0;
        error_count = 0;

        // Assert reset, then deassert
        #10; reset = 0;

        // Define data streams and expected outputs
        data_stream[0] = 8'b01111100; // Test discard
        data_stream[1] = 8'b01111110; // Test flag
        data_stream[2] = 8'b011111110; // Test error
        data_stream[3] = 8'b001111111; // No discard, no flag, error
        data_stream[4] = 8'b01111110; // Test flag again
        data_stream[5] = 8'b00000000; // Nothing

        // Expected results
        for (i = 0; i < 6; i = i + 1) begin
            #(10);  // Start new input cycle
            send_data_stream(data_stream[i]);

            // Check outputs
            #(10); // Allow for outputs to register
            check_outputs(data_stream[i]);
        end

        if (error_count == 0)
            $display("all test cases passed");
        $finish;
    end

    task send_data_stream;
        input [7:0] data_stream;
        integer j;
        begin
            for (j = 7; j >= 0; j = j - 1) begin
                in = data_stream[j];
                #(10);
            end
        end
    endtask

    task check_outputs;
        input [7:0] data_stream;
        begin
            if ((data_stream === 8'b01111100) && (!disc || flag || err))
                error_count = error_count + 1;
            if ((data_stream === 8'b01111110) && (disc || !flag || err))
                error_count = error_count + 1;
            if ((data_stream === 8'b011111110) && (disc || flag || !err))
                error_count = error_count + 1;
            if ((data_stream === 8'b001111111) && (disc || flag || !err))
                error_count = error_count + 1;
        end
    endtask

