        @(posedge clk); // Wait two clock cycles for ff
        check_outputs(1, 1, 0);

        // Test Case 3
        a = 1; b = 0;
        @(posedge clk);
        @(posedge clk); // Wait two clock cycles for ff
        check_outputs(1, 1, 1);

        // Test Case 4
        a = 1; b = 1;
        @(posedge clk);
        @(posedge clk); // Wait two clock cycles for ff
        check_outputs(0, 0, 0);

        #10 $finish; // Finish simulation
    end

    // Task to check outputs and display results
    task check_outputs;
        input expected_assign, expected_comb, expected_ff_before;
        begin
            if (out_assign === expected_assign && out_always_comb === expected_comb && out_always_ff === expected_ff_before) begin
                $display("Test case passed.");
            end else begin
                $display("Test case FAILED: Expected out_assign=%b, out_always_comb=%b, out_always_ff=%b; Received out_assign=%b, out_always_comb=%b, out_always_ff=%b",
                    expected_assign, expected_comb, expected_ff_before, out_assign, out_always_comb, out_always_ff);
                $finish; // Terminate simulation after a failure
            end
        end
    endtask

    initial begin
        // Here we replace the problematic wait-statement with monitoring changes, then decide when to end the test or display results.
        #1000; // Allow sufficient time for all test cases then end the simulation regardless of results to avoid indefinite hanging.
        $display("Time-out: Simulation ended without explicitly passing all test cases.");
        $finish;
    end

endmodule

