module testbench;
    reg clk, reset, data, done_counting, ack;
    wire shift_ena, counting, done;

    top_module DUT (
        .clk(clk),
        .reset(reset),
        .data(data),
        .shift_ena(shift_ena),
        .counting(counting),
        .done_counting(done_counting),
        .done(done),
        .ack(ack)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Test sequence
    initial begin
        reset = 1; data = 0; done_counting = 0; ack = 0;
        #10;
        reset = 0;

        // Test for starting sequence detection (1101)
        push_bits(4'b0101);
        #10
        push_bits(4'b1101);
        #10
        if (shift_ena !== 1'b0) begin
            $display("Test 1 Failed");
            $finish;
        end

        // Push more bits and check for shift_ena
        push_bits(4'b1101);
        for (integer i = 0; i < 4; i++) begin
            #(10); 
            data = 1;
            if (i < 4 && shift_ena !== 1'b1) begin
                $display("Test 2 Failed at cycle %d", i);
                $finish;
            end
        end

        // Check that shift_ena deasserts after four cycles
        #(10);
        data = 0;
        if (shift_ena !== 1'b0) begin
            $display("Test 3 Failed");
            $finish;
        end

        // Test counting initiation
        #(10);
        if (counting !== 1'b1) begin
            $display("Test 4 Failed");
            $finish;
        end

        // Simulating the counters finishing
        #(10);
        done_counting = 1;
        #(10);
        if (done !== 1'b1) begin
            $display("Test 5 Failed");
            $finish;
        end

        // Acknowledgment process
        ack = 1;
        #(10);
        if (counting !== 1'b0 || done !== 1'b0) begin
            $display("Test 6 Failed");
            $finish;
        end

        // Wait to prevent premature test case pass display
        #(10);
        $display("all test cases passed");
    end

    task push_bits(input [3:0] bits);
        for (integer i = 3; i >= 0; i--) begin
            data = bits[i];
            #(10);
        end
    endtask
