    top_module uut (
        .clk(clk),
        .reset(reset),
        .s(s),
        .w(w),
        .z(z)
    );

    // Clock generation
    always #5 clk = ~clk;

    // Task to initialize signals
    task initialize;
        begin
            clk = 0;
            reset = 0;
            s = 0;
            w = 0;
        end
    endtask

    // Task to apply reset
    task apply_reset;
        begin
            reset = 1;
            #10;
            reset = 0;
        end
    endtask

    // Test Cases
    integer errors = 0;

    task test_case_1;
        begin
            // Test Case 1: Check correct functionality from state A to B and count two w=1 in three cycles
            apply_reset;
            s = 1;  w = 1;  #10;  // Move to B, first w
            w = 0;  #10;         // second cycle
            w = 1;  #10;         // Third cycle, second w=1
            w = 0;  #10;         // z should now be 1

            if (z !== 1) begin
                $display("Test Case 1 Failed");
                errors = errors + 1;
            end
        end
    endtask

    task test_case_2;
        begin
            // Test Case 2: No successive two w=1s in three cycles
            apply_reset;
            s = 1;  w = 0;  #10;
            w = 1;  #10;
            w = 0;  #10;
            w = 1;  #10;   // z should be 0
            if (z !== 0) begin
                $display("Test Case 2 Failed");
                errors = errors + 1;
            end
        end
    endtask

    // Main test sequence
    initial begin
        initialize;
        apply_reset;
        test_case_1;
        test_case_2;

        // Check if all test cases passed
        #10;  // Wait to account for any delayed reaction in DUT
        if (errors == 0) begin
            $display("all test cases passed");
        end
        $finish;  // Terminate simulation
    end

