module testbench;
    reg a, b, c, d, e;
    wire [24:0] out;

    top_module dut (
        .a(a),
        .b(b),
        .c(c),
        .d(d),
        .e(e),
        .out(out)
    );

    reg [24:0] expected_output;

    // Function to calculate all pairwise comparisons
    function [24:0] calculate_output(input a, b, c, d, e);
        integer idx;
        reg [4:0] v;
        begin
            v = {a, b, c, d, e};
            calculate_output = 25'b0;

            for (idx = 0; idx < 5; idx = idx + 1) begin
                calculate_output = (calculate_output << 5) | ({~v[idx]^v[4], ~v[idx]^v[3], ~v[idx]^v[2], ~v[idx]^v[1], ~v[idx]^v[0]});
            end
        end
    endfunction

    // Task to perform the test
    task perform_test;
        integer i, fail;
        begin
            fail = 0;
            a = $random;
            b = $random;
            c = $random;
            d = $random;
            e = $random;

            expected_output = calculate_output(a, b, c, d, e);
            #10; // Wait 10 ns for output to stabilize

            if (out != expected_output) begin
                fail = 1;
            end

            if (fail == 0) begin
                $display("all test cases passed");
            end
        end
    endtask

    // Initial block to run the test multiple times
    integer j;
    initial begin
        // Run 100 different random test cases
        for (j = 0; j < 100; j = j + 1) begin
            perform_test();
        end
    end

endmodule

