module testbench;
    reg clk, j, k;
    reg Qold;
    wire Q;
    integer errors = 0;

    top_module dut(
        .clk(clk),
        .j(j),
        .k(k),
        .Q(Q)
    );

    initial begin
        // Initialize signals
        clk = 0;
        j = 0;
        k = 0;
        Qold = 0;

        // Generate clock
        forever #5 clk = ~clk;
    end

    initial begin
        // Wait for the clock to stabilize
        #10;

        // Test case 1: J=0, K=0 => Q should be Qold
        j = 0; k = 0; Qold = Q;
        #10; // wait for next clock edge
        if (Q !== Qold) begin
            $display("Test case 1 failed: J=0, K=0, Qold=%b, Q=%b", Qold, Q);
            errors = errors + 1;
        end

        // Test case 2: J=0, K=1 => Q should be 0
        j = 0; k = 1;
        #10;
        if (Q !== 0) begin
            $display("Test case 2 failed: J=0, K=1, Q=%b", Q);
            errors = errors + 1;
        end

        // Test case 3: J=1, K=0 => Q should be 1
        j = 1; k = 0;
        #10;
        if (Q !== 1) begin
            $display("Test case 3 failed: J=1, K=0, Q=%b", Q);
            errors = errors + 1;
        end

        // Test case 4: J=1, K=1 => Q should be ~Qold
        Qold = Q;
        j = 1; k = 1;
        #10;
        if (Q !== ~Qold) begin
            $display("Test case 4 failed: J=1, K=1, Qold=%b, Q=%b", Qold, Q);
