    top_module uut (
        .clk(clk),
        .reset(reset),
        .q(q)
    );

    // Clock generation
    always begin
        clk = 0; #5;
        clk = 1; #5;
    end

    // Monitor to check for all-zero state (which should never be reached)
    integer test_fail_flag = 0;
    always @(posedge clk) begin
        if (q == 5'b00000) begin
            test_fail_flag = 1;
        end
    end

    // Check the sequence, and reset
    initial begin
        // Initialize Inputs
        reset = 1;
        #17; // Sufficient time after reset
        reset = 0;

        // Wait for LFSR to cycle through patterns
        repeat (31) @(posedge clk);

        @ (posedge clk) if (q !== 5'b00001) test_fail_flag = 1;
        @ (posedge clk) if (q !== 5'b10000) test_fail_flag = 1;
        @ (posedge clk) if (q !== 5'b11000) test_fail_flag = 1;
        @ (posedge clk) if (q !== 5'b11100) test_fail_flag = 1;
        @ (posedge clk) if (q !== 5'b01110) test_fail_flag = 1;
        @ (posedge clk) if (q !== 5'b00111) test_fail_flag = 1;
        @ (posedge clk) if (q !== 5'b10011) test_fail_flag = 1;
        @ (posedge clk) if (q !== 5'b11001) test_fail_flag = 1;
        @ (posedge clk) if (q !== 5'b11100) test_fail_flag = 1;
        @ (posedge clk) if (q !== 5'b01110) test_fail_flag = 1;
        @ (posedge clk) if (q !== 5'b00111) test_fail_flag = 1;
        // Continue the specific LFSR sequence verification as above (complete the pattern repetition)

        // Additional cycles to observe and further determine no incorrect states are reached
        repeat (10) @ (posedge clk);

        // If no failures, then all test cases passed
        if (!test_fail_flag) $display("all test cases passed");

