        .pos(pos)
    );

    initial begin
        error_count = 0;
        // Test all possible 4-bit combinations
        for (i = 0; i < 16; i = i + 1) begin
            in = i;
            #10; // wait for DUT to process

            // calculate expected position of the first '1' from the left
            case(in)
                4'b0001: expected_pos = 2'd0;
                4'b0010: expected_pos = 2'd1;
                4'b0100: expected_pos = 2'd2;
                4'b1000: expected_pos = 2'd3;
                4'b0011: expected_pos = 2'd1;
                4'b0101: expected_pos = 2'd2;
                4'b0110: expected_pos = 2'd1;
                4'b1001: expected_pos = 2'd3;
                4'b1010: expected_pos = 2'd3;
                4'b1100: expected_pos = 2'd3;
                4'b0111: expected_pos = 2'd1;
                4'b1011: expected_pos = 2'd3;
                4'b1101: expected_pos = 2'd3;
                4'b1110: expected_pos = 2'd3;
                4'b1111: expected_pos = 2'd3;
                default: expected_pos = 2'd0; // In case of in = 4'b0000
            endcase

            if (pos !== expected_pos) begin
                error_count = error_count + 1;
            end
        end

        // Check if any test case failed
        if (error_count == 0) begin
            $display("all test cases passed");
        end
        $finish; // Terminate simulation
    end

