module testbench;
    reg clk, areset, bump_left, bump_right, ground, dig;
    wire walk_left, walk_right, aaah, digging;

    top_module DUT (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Generate clock
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock period is 10ns
    end

    // Stimulus
    initial begin
        // Initialize all signals
        areset = 1; #2;
        areset = 0; // release reset
        bump_left = 0;
        bump_right = 0;
        ground = 1;
        dig = 0;

        // Test Case 1: Should default start walking left
        #10;
        if (walk_left !== 1'b1 || walk_right !== 1'b0) begin
            $display("Test Case 1 Failed: Initialization incorrect.");
            $finish;
        end

        // Test Case 2: Bump on the left should cause walking right
        bump_left = 1;
        #10; bump_left = 0;
        if (walk_left !== 1'b0 || walk_right !== 1'b1) begin
            $display("Test Case 2 Failed: Bumping left does not work.");
            $finish;
        end

        // Test Case 3: Lemming falls if no ground
        ground = 0;
        #10;
        if (aaah !== 1'b1) begin
            $display("Test Case 3 Failed: Falling logic incorrect.");
            $finish;
        end

        // Restore ground, should continue walking right
        ground = 1;
        #10;
        if (walk_left !== 1'b0 || walk_right !== 1'b1) begin
            $display("Test Case 4 Failed: Resume walking logic incorrect.");
            $finish;
        end

        // Test Case 4: Bump on the right while walking right should turn left
        bump_right = 1;
        #10; bump_right = 0;
        if (walk_left !== 1'b1 || walk_right !== 1'b0) begin
            $display("Test Case 5 Failed: Bumping right does not work.");
            $finish;
        end

        // Test Case 5: Valid dig command
        dig = 1;
        #20; dig = 0; // holds dig for 2 clock periods
        if (digging !== 1'b1) begin
            $display("Test Case 6 Failed: Digging logic incorrect.");
            $finish;
        end

        // Lose ground while digging
        ground = 0;
        #10;
        if (aaah !== 1'b1) begin
            $display("Test Case 7 Failed: Falling while digging logic incorrect.");
            $finish;
        end

        // Display all test cases passed if none have failed
        $display("all test cases passed");
        $finish;
    end

endmodule

