module testbench;

reg clk, reset;
reg [7:0] in;
wire [23:0] out_bytes;
wire done;

// Instantiate the Device Under Test (DUT)
top_module DUT (
    .clk(clk),
    .in(in),
    .reset(reset),
    .out_bytes(out_bytes),
    .done(done)
);

// Clock generation
initial begin
    clk = 0;
    forever #5 clk = ~clk; // Clock with period 10ns
end

// Test cases
initial begin
    reset = 1;
    in = 8'h00;

    // Apply Reset
    @(posedge clk); 
    @(posedge clk);
    reset = 0;

    @(posedge clk); in = 8'h2c; // Normal data byte
    @(posedge clk); 
    @(posedge clk); in = 8'h81; // Start of a new message
    @(posedge clk);
    @(posedge clk); in = 8'h09; // Second byte of the message
    @(posedge clk);
    @(posedge clk); in = 8'h6b; // Third byte, message should complete here
    @(posedge clk);
    if (done !== 1'b1 || out_bytes !== 24'h2c8109) $display("Test case failed.");

    // Continue with more input bytes alternating between valid and non-valid message starts
    @(posedge clk); in = 8'h0d;
    @(posedge clk);
    @(posedge clk); in = 8'h8d;
    @(posedge clk);
    @(posedge clk); in = 8'h6d; // Another new message, should see done
    @(posedge clk);
    if (done !== 1'b1 || out_bytes !== 24'h6b0d8d) $display("Test case failed.");

    @(posedge clk); in = 8'h12;
    @(posedge clk);
    @(posedge clk); in = 8'h01;
    @(posedge clk);
    @(posedge clk); in = 8'h0d; // Done expected here
    @(posedge clk);
    if (done !== 1'b1 || out_bytes !== 24'h6d1201) $display("Test case failed.");

    @(posedge clk); in = 8'h76;
    @(posedge clk);
    @(posedge clk); in = 8'h3d;
    @(posedge clk);
    @(posedge clk); in = 8'hed; // Done, testing last series
    @(posedge clk);
    if (done !== 1'b1 || out_bytes !== 24'hd763d) $display("Test case failed.");

    @(posedge clk); in = 8'h8c;
    @(posedge clk);
    @(posedge clk); in = 8'hf9;
    @(posedge clk);
    @(posedge clk); in = 8'hce; // Final valid message check
    @(posedge clk);
    if (done !== 1'b1 || out_bytes !== 24'hed8cf9) $display("Test case failed.");

    @(posedge clk);
    // Check if all test cases passed
    if (!$test$plusargs("Test case failed.")) $display("all test cases passed");

    $finish; // End the simulation
end

endmodule

