    #10 a = 1; expected_q = 0;
    #10 a = 1; expected_q = 0;
    #10 a = 1; expected_q = 0;

    // Final time to allow checking last event
    #10;

    if (errors == 0) begin
      $display("all test cases passed");
    end
  end

  // Monitoring and Checking mechanism
  always @(posedge clk) begin
    if (q !== expected_q) begin
      $display("Mismatch at %t: expected q = %b, got q = %b", $time, expected_q, q);
      errors = errors + 1;
    end
  end

  initial begin
    #200 $finish;  // End simulation at 200ns
  end

endmodule

