    // Initialize inputs
    resetn = 0;
    r = 3'b000;

    // Reset the FSM
    #10;
    resetn = 1;

    // Test Cases 
    // Test1: Check state A with no request
    #10;
    r = 3'b000;
    if ((g != 3'b000)) $display("Test Case 1 Failed");

    // Test2: Device 1 requests, check grant
    #10;
    r = 3'b100;
    if ((g != 3'b100)) $display("Test Case 2 Failed");

    // Test3: Device 1 continues, check grant remains
    #10;
    r = 3'b100;
    if ((g != 3'b100)) $display("Test Case 3 Failed");

    // Test4: Device 1 drops, check for state A grants
    #10;
    r = 3'b000;
    if ((g != 3'b000)) $display("Test Case 4 Failed");

    // Test5: Device 2 only requests
    #10;
    r = 3'b010;
    if ((g != 3'b010)) $display("Test Case 5 Failed");

    // Test6: Device 3 only requests
    #10;
    r = 3'b001;
    if ((g != 3'b001)) $display("Test Case 6 Failed");

    // Test7: Multiple devices request, check priority
    #10;
    r = 3'b011; // Both Device 2 and 3 request
    if ((g != 3'b010)) $display("Test Case 7 Failed");

    #10;
    r = 3'b111; // All devices request
    if ((g != 3'b100)) $display("Test Case 8 Failed");

    // Test8: Checking reset
    #10;
    resetn = 0;
    #10;
    resetn = 1;
    if (g != 3'b000) $display("Test Case 9 Failed");

    // If no failure messages have been displayed, all test cases passed.
    #10;
    $finish; // End simulation
end

// Check for passing all test cases
