module TestBench;

  reg [99:0] a, b;
  reg sel;
  wire [99:0] out;
  integer i;
  reg [99:0] expected_out;
  reg error_flag = 0;

  top_module DUT (
    .a(a),
    .b(b),
    .sel(sel),
    .out(out)
  );

  initial begin
    // Test Case 1: sel = 0, choose a
    a = 100'h3; // Hexadecimal for binary with 100-bits where lower bits are 11
    b = 100'h7; // Hexadecimal for binary with 100-bits where lower bits are 111
    sel = 0;
    expected_out = a;
    #10;   // Wait for propagation
    if (out !== expected_out) begin   // !== is used to detect any X or Z values.
      error_flag = 1;
    end

    // Test Case 2: sel = 1, choose b
    sel = 1;
    expected_out = b;
    #10;   // Wait for propagation
    if (out !== expected_out) begin
      error_flag = 1;
    end

    // Arbitrary number of additional cases
    for (i = 0; i < 100; i = i + 1) begin
      a = $random;
      b = $random;
      sel = $random % 2;   // Randomly choosing sel as 0 or 1
      expected_out = sel ? b : a;
      #10;
      if (out !== expected_out) begin
        error_flag = 1;
        break;   // Exit if error occurs
      end
    end

    if (error_flag == 0)
      $display("all test cases passed");
  end

endmodule

