module tb_top_module;

    // Inputs
    reg clk;
    reg x;

    // Outputs
    wire z;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .x(x),
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100 MHz Clock
    end

    // Initialize Inputs and Test Cases
    initial begin
        // Initialize x
        x = 0;

        // Wait for global reset
        #(10); 

        // Test Case 1: Check Button Zero Start
        x = 0; #(10);
        check_result(z, 1'b1);

        // Test Case 2: Check Button Press
        x = 1; #(10);
        check_result(z, 1'b0);

        // Test Case 3: Flip Button Press
        x = 0; #(10);
        check_result(z, ~z);

        // Test Case 4: Repeat Flip
        x = 1; #(10);
        check_result(z, ~z);

        // Test Case 5: Back to original state
        x = 0; #(10);
        check_result(z, z);

        // If all tests pass, print this
        $display("all test cases passed");

        // End simulation
        $finish;
    end

    // Task for checking the result and displaying the assertion fail
    integer tests_failed = 0;
    task check_result;
        // Declare input variables as reg type instead of wire to fix the syntax error
        input reg observed;
        input reg expected;
        begin
            if(observed !== expected) begin
                $display("Test failed: Expected %b, got %b at time %t", expected, observed, $time);
                tests_failed = tests_failed + 1;
            end
        end
    endtask

    // Before finish, check if any tests failed
    initial begin
        #100;
        if(tests_failed > 0) $display("Some test cases failed.");
    end

endmodule

