        .in(in),
        .out_both(out_both),
        .out_any(out_any),
        .out_different(out_different)
    );

    // Function to initialize tests
    function automatic void initialize();
        in = 100'd0;  // Initialize input to zero
    endfunction

    // Task to run each test case
    task automatic run_test();
        integer i;

        // Generate all combinations of input vector and verify outputs
        for (i = 0; i < 2**100; i = i + 1) begin
            in = i;  // Test all possible input combinations
            #10;  // Wait some time for outputs to settle

            // Check all output conditions
            if (out_both !== check_out_both() || out_any !== check_out_any() || out_different !== check_out_different()) begin
                $display("Test failed for input: %b", in);
                return;
            end
        end

        // If all tests passed
        $display("all test cases passed");
    endtask

    // Expected results functions
    function [98:0] check_out_both();
        integer j;
        for (j = 0; j <= 98; j = j + 1) begin
            check_out_both[j] = (in[j] && in[j+1]);
        end
    endfunction

    function [99:1] check_out_any();
        integer k;
        for (k = 1; k <= 99; k = k + 1) begin
            check_out_any[k] = (in[k] || in[k-1]);
        end
    endfunction

    function [99:0] check_out_different();
        integer m;
        for (m = 0; m <= 98; m = m + 1) begin
            check_out_different[m] = (in[m] != in[m+1]);
        end
        check_out_different[99] = (in[99] != in[0]);  // Wrapped around
    endfunction

    // Test execution
    initial begin
        initialize();
        run_test();
        $finish;  // Terminate simulation
    end

