    top_module dut (
        .clk(clk),
        .reset(reset),
        .data(data),
        .count(count),
        .counting(counting),
        .done(done),
        .ack(ack)
    );

    // Clock generation
    always #5 clk = !clk;  // Clock period of 10ns

    // Test vectors and monitoring
    initial begin
        clk = 0;
        reset = 1;
        data = 0;
        ack = 0;

        // Apply reset
        #20;
        reset = 0;

        // Test case 1: Sequence detection and counting
        // Send 1101 + 4'b1010 (delay=10)
        force data = 1; #10;
        force data = 1; #10;
        force data = 0; #10;
        force data = 1; #10;
        // Send delay bits
        force data = 1; #10;
        force data = 0; #10;
        force data = 1; #10;
        force data = 0; #10;
        release data;

        // Check if counting starts and delay is correct
        #20000; // 20000ns should account for sequence check + delay
        if (counting !== 1'b1 || count !== 4'd10) begin
            $display("Test case 1 failed.");
            $finish;
        end

        // Wait for timer to count down
        #(10000 * 11); // (10+1)*1000 cycles period to finish the count down
        if (done !== 1'b1) begin
            $display("Test case 1 failed. Done not asserted correctly.");
            $finish;
        end

        // Acknowledge the timer
        force ack = 1; #10;
        release ack;

        // Test case 2: Reset functionality
        #100;
        reset = 1; #20;
        reset = 0;
        // Check if the system is ready to receive new data sequentially
        // Send a new set of data: 1101 and 4'b0011
        force data = 1; #10;
        force data = 1; #10;
        force data = 0; #10;
        force data = 1; #10;
        // Send delay bits
        force data = 0; #10;
        force data = 0; #10;
        force data = 1; #10;
        force data = 1; #10;

        #16000; // Wait for new process to (3+1) * 1000 clock cycles
        if (done !== 1'b1) begin
            $display("Test case 2 failed.");
            $finish;
        end

        // All test cases passed
