    top_module dut(
        .clk(clk),
        .areset(areset),
        .predict_valid(predict_valid),
        .predict_pc(predict_pc),
        .predict_taken(predict_taken),
        .predict_history(predict_history),
        .train_valid(train_valid),
        .train_taken(train_taken),
        .train_mispredicted(train_mispredicted),
        .train_history(train_history),
        .train_pc(train_pc)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = !clk; // Clock period of 10 ns
    end

    initial begin
        areset = 1;
        #10;
        areset = 0;
    end

    // Test Cases
    initial begin
        // Initialize all inputs
        predict_valid = 0;
        predict_pc = 0;
        train_valid = 0;
        train_taken = 0;
        train_mispredicted = 0;
        train_history = 0;
        train_pc = 0;

        // Asynchronous reset
        areset = 1; #10;
        areset = 0; #10;

        // Test Sequence

        // Test 1: Prediction when the branch predictor is initially reset.
        predict_valid = 1;
        predict_pc = 7'b0101010;
        #10;
        predict_valid = 0;
        #10;

        // Test 2: Training the predictor
        train_valid = 1;
        train_taken = 1;
        train_mispredicted = 0;
        train_history = predict_history; // use the history from the first prediction
        train_pc = predict_pc;
        #10;
        train_valid = 0;
        #10;

        // Test 3: Prediction after training
        predict_valid = 1;
        #10;
        predict_valid = 0;
        #10;

        // Check test results
        #100;
        if (final_check_passed) begin
            $display("all test cases passed");
        end
    end

    reg final_check_passed = 1;

    // Monitor and check outcomes
    always @(posedge clk) begin
        if (train_valid & predict_valid & (train_pc == predict_pc)) begin
            if (predict_taken !== predict_history) begin
                final_check_passed = 0;
            end
        end

        // Similar conditional checks for other scenarios can be added here
    end

    // Terminate simulation
    initial begin
        #1000
        $finish;
    end

endmodule

