module tb_top_module;

  reg clk;
  reg reset;
  reg in;
  wire disc;
  wire flag;
  wire err;

  // DUT instance
  top_module dut(
    .clk(clk),
    .reset(reset),
    .in(in),
    .disc(disc),
    .flag(flag),
    .err(err)
  );

  // Clock generation
  initial begin
    clk = 0;
    forever #5 clk = ~clk; // Generate clock with 10 ns period
  end

  // Test cases
  initial begin
    reset = 1; #15;
    reset = 0; #10;

    // Test Case 1: Simple flag sequence
    drive_bits("01111110", 8);  // Expected: flag asserted
    check_outputs(0, 1, 0);

    // Test Case 2: Discard bit insertion sequence
    drive_bits("01111110", 7); // Driving one less bit to check discard on the next clock
    check_outputs(0, 0, 0);
    in = 1; #10; // This is the 7th '1'
    check_outputs(1, 0, 0); // Expected: disc asserted

    // Test Case 3: Error sequence (>6 ones)
    drive_bits("011111111", 9);  // Expected: error asserted
    check_outputs(0, 0, 1);

    // Validate test results
    if (test_fail == 1) begin
      $display("Test cases have failures.");
    end else begin
      $display("all test cases passed");
    end

    $finish;
  end

  // Helper task to drive input bits
  task drive_bits;
    input [1023:0] bits;
    input integer num_bits;
    integer i;
    begin
      for (i = 0; i < num_bits; i = i + 1) begin
        in = bits[i];
        #10;
      end
    end
  endtask

  // Helper task to check the outputs against expected results
  reg test_fail = 0;
  task check_outputs;
    input expected_disc;
    input expected_flag;
    input expected_err;
    begin
      #10;  // Wait for output to stabilize
      if ((disc !== expected_disc) || (flag !== expected_flag) || (err !== expected_err)) begin
        $display("Error: Output mismatch at time %t", $time);
        $display("Expected - disc: %b, flag: %b, err: %b", expected_disc, expected_flag, expected_err);
        $display("Got      - disc: %b, flag: %b, err: %b", disc, flag, err);
        test_fail = 1;
      end
    end
  endtask

endmodule

