        .c(c),
        .d(d),
        .out(out)
    );

    initial begin
        // Initialize Inputs
        a = 0; b = 0; c = 0; d = 0;

        // Apply test cases based on the Karnaugh Map
        // Testing each combination and checking output
        // Values d (don't care) set to 0 for testing

        // ab=01, cd=00; out should be d(don't-care)
        a = 0; b = 1; c = 0; d = 0; #10;

        // ab=00, cd=00; out should be 0
        a = 0; b = 0; c = 0; d = 0; #10;
        if (out !== 0) $display("Test case ab=00, cd=00 failed");

        // ab=10, cd=00; out should be 1
        a = 1; b = 0; c = 0; d = 0; #10;
        if (out !== 1) $display("Test case ab=10, cd=00 failed");

        // ab=11, cd=00; out should be 1
        a = 1; b = 1; c = 0; d = 0; #10;
        if (out !== 1) $display("Test case ab=11, cd=00 failed");

        // ab=01, cd=01; out should be 0
        a = 0; b = 1; c = 0; d = 1; #10;
        if (out !== 0) $display("Test case ab=01, cd=01 failed");

        // ab=00, cd=01; out should be 0
        a = 0; b = 0; c = 0; d = 1; #10;
        if (out !== 0) $display("Test case ab=00, cd=01 failed");

        // ab=11, cd=01; out should be d (testing as both 0 and 1)
        a = 1; b = 1; c = 0; d = 1; #10;

        // ab=10, cd=01; out should be d (testing as both 0 and 1)
        a = 1; b = 0; c = 0; d = 1; #10;

        // ab=00, cd=11; out should be 0
        a = 0; b = 0; c = 1; d = 1; #10;
        if (out !== 0) $display("Test case ab=00, cd=11 failed");

        // ab=01, cd=11; out should be 1
        a = 0; b = 1; c = 1; d = 1; #10;
        if (out !== 1) $display("Test case ab=01, cd=11 failed");

        // ab=11, cd=11; out should be 1
        a = 1; b = 1; c = 1; d = 1; #10;
        if (out !== 1) $display("Test case ab=11, cd=11 failed");

        // ab=10, cd=11; out should be 1
        a = 1; b = 0; c = 1; d = 1; #10;
        if (out !== 1) $display("Test case ab=10, cd=11 failed");

        // ab=00, cd=10; out should be 1
        a = 0; b = 0; c = 1; d = 0; #10;
        if (out !== 1) $display("Test case ab=00, cd=10 failed");

        // ab=01, cd=10; out should be 1
        a = 0; b = 1; c = 1; d = 0; #10;
        if (out !== 1) $display("Test case ab=01, cd=10 failed");

        // ab=11, cd=10; out should be 1
        a = 1; b = 1; c = 1; d = 0; #10;
        if (out !== 1) $display("Test case ab=11, cd=10 failed");

        // ab=10, cd=10; out should be 1
        a = 1; b = 0; c = 1; d = 0; #10;
        if (out !== 1) $display("Test case ab=10, cd=10 failed");

        $display("all test cases passed");
    end

