module tb_top_module;
    reg clk, in, areset;
    wire out;

    top_module dut(
        .clk(clk),
        .in(in),
        .areset(areset),
        .out(out)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100 MHz clock
    end

    // Test cases
    initial begin
        areset = 1; // Assert reset
        #10;
        areset = 0; // De-assert reset
        if (out !== 0) begin
            $display("Test Case failed: Expected output 0 at initial reset");
            $finish;
        end

        // State A tests
        in = 0; #10; // A -> A
        if (out !== 0) $display("Test case failed: State A, input 0");
        in = 1; #10; // A -> B
        if (out !== 0) $display("Test case failed: State A to B, input 1");

        // State B tests
        in = 0; #10; // B -> C
        if (out !== 0) $display("Test case failed: State B to C, input 0");
        in = 1; #10; // B -> B
        if (out !== 0) $display("Test case failed: State B, input 1");

        // State C tests
        in = 0; #10; // C -> A
        if (out !== 0) $display("Test case failed: State C to A, input 0");
        in = 1; #10; // C -> D
        if (out !== 0) $display("Test case failed: State C to D, input 1");

        // State D tests
        in = 0; #10; // D -> C
        if (out !== 1) $display("Test case failed: State D to C, input 0");
        in = 1; #10; // D -> B
        if (out !== 1) $display("Test case failed: State D to B, input 1");

        // Reset Check
        areset = 1; #10; // Asynchronous reset to state A
        if (out !== 0) $display("Test case failed: Asynchronous reset to state A");
        areset = 0;
        in = 1; #10; // A -> B
        if (out !== 0) $display("Test case failed: State A to B after reset, input 1");

        // All tests passed
        $display("all test cases passed");
        $finish;
    end
