module testbench;

    reg [99:0] in;
    wire [98:0] out_both;
    wire [99:1] out_any;
    wire [99:0] out_different;
    integer i;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .in(in),
        .out_both(out_both),
        .out_any(out_any),
        .out_different(out_different)
    );

    // Task to check output values
    task check_outputs;
        reg [98:0] expected_both;
        reg [99:1] expected_any;
        reg [99:0] expected_different;
        reg test_failed;
        begin
            test_failed = 0;

            // Compute expected outputs
            for (i = 0; i < 99; i = i + 1) begin
                expected_both[i] = in[i] & in[i+1];
                expected_any[i+1] = in[i] | in[i+1];
                expected_different[i] = in[i] ^ in[i+1];
            end
            expected_different[99] = in[99] ^ in[0];

            // Check and report results
            for (i = 0; i < 99; i = i + 1) begin
                if (out_both[i] !== expected_both[i] || out_any[i+1] !== expected_any[i+1]) begin
                    test_failed = 1;
                end
            end

            for (i = 0; i <= 99; i = i + 1) begin
                if (out_different[i] !== expected_different[i]) begin
                    test_failed = 1;
                end
            end

            if (test_failed === 0) begin
                $display("all test cases passed");
            end
        end
    endtask

    // Initialize and run test scenarios
    initial begin
        // Test case 1 (Random values)
        in = 100'b1101010010100011100101010101010010110010101010101010111001010101010101010101010111010101010101010;
        #10 check_outputs;

        // Test case 2 (All zeros)
        in = 100'b0;
        #10 check_outputs;

        // Test case 3 (All ones)
        in = 100'b1111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111;
        #10 check_outputs;

        // Test case 4 (Alternating bits starting with 0)
        in = 100'b1010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101;
        #10 check_outputs;

        // Test case 5 (Alternating bits starting with 1)
        in = 100'b0101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010;
        #10 check_outputs;

        #100 $finish;
    end

endmodule

