module testbench;

    reg [4:1] x;
    wire f;
    integer i, err = 0;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .x(x),
        .f(f)
    );

    initial begin
        // Define all test cases based on the Karnaugh map
        // x[4]x[3]x[2]x[1] | Expected f output
        // Note: x[1] is the LSB
        // 0000 -> f = 1, 0001 -> f = 0, 0011 -> f = 0, 0010 -> f = 1
        // 0100 -> f = 0, 0101 -> f = 0, 0111 -> f = 0, 0110 -> f = 0
        // 1100 -> f = 1, 1101 -> f = 1, 1111 -> f = 1, 1110 -> f = 0
        // 1000 -> f = 1, 1001 -> f = 1, 1011 -> f = 0, 1010 -> f = 1

        // Test vector array
        reg [15:0] test_vector[0:1]; // 16 test cases
        // Lower bits are test input, upper single bit is expected output
        test_vector[0] = 16'b1000_0000_0001_0001;
        test_vector[1] = 16'b1111_1111_1111_0000;

        for (i = 0; i < 16; i = i + 1) begin
            x = i[3:0]; // input test
            #10; // wait for output to stabilize
            if (f !== test_vector[i/8][i%8]) begin
                $display("Test case %d failed: input=%b, expected output=%b, received output=%b",
                         i, x, test_vector[i/8][i%8], f);
                err = 1;
            end
        end

        if (err === 0) begin
            $display("all test cases passed");
        end

        #10;
        $finish;
    end

endmodule

