        #10;
        if (out != 8'h00) begin
            $display("Test Case 3: Subtraction to Zero Error: Expected %h, Got %h", 8'h00, out);
            errors = errors + 1;
        end
        if (result_is_zero != 1) begin
            $display("Test Case 3: Zero Flag not Set");
