    top_module dut (
        .clock(clock),
        .a(a),
        .p(p),
        .q(q)
    );

    // Clock generation
    initial begin
        clock = 0; // Initial state of the clock
        forever #5 clock = ~clock; // Toggle every 5 ns -> 100 MHz
    end

    // Stimulus block
    initial begin
        // Initialize Input
        a = 0;

        // Wait for Global Reset to finish
        #(25) a = 0; // Match Table - until 25ns
        #10 a = 0; // until 35ns exact
        #35 a = 1; // at 70ns set to 1
        #10 a = 0; // at 80ns set to 0
        #5 a = 1;  // at 85ns set to 1
        #5 a = 0;  // at 90ns set to 0
        #5 a = 1;  // at 95ns set to 1
        #15 a = 0; // at 110ns set to 0
        #5 a = 1;  // at 115ns set to 1
        #5 a = 0;  // at 120ns set to 0
        #5 a = 1;  // at 125ns set to 1
        #15 a = 0; // at 140ns set to 0
        #5 a = 1;  // at 145ns set to 1
        #5 a = 0;  // at 150ns resume 0
        #20 a = 1; // at 170ns set to 1
        #5 a = 0;  // at 175ns set to 0
        #5 a = 1;  // at 180ns set to 1
        #10 a = 0; // at 190ns back to 0
        #10; // Allow time for all processes to complete
    end

    // Monitor block to check output validity
    initial begin
        // Initial Assertion
        $monitor("At time %t, clock = %d, a = %d -> p = %d, q = %d", $time, clock, a, p, q);

        // Testing the outputs
        #200; // Test at 200ns
