module testbench;
    reg clk, areset, train_valid, train_taken;
    wire [1:0] state;
    reg [1:0] expected_state;
    reg test_failed;

    // Include the DUT
    top_module dut(
        .clk(clk),
        .areset(areset),
        .train_valid(train_valid),
        .train_taken(train_taken),
        .state(state)
    );

    // Clock definition
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock period of 10ns
    end

    // Test sequences
    initial begin
        test_failed = 0;
        areset = 1; train_valid = 0; train_taken = 0; 
        #10;     // Wait 10ns to stabilize reset
        areset = 0;

        // Test 1: Check reset condition
        expected_state = 2'b01;
        verify_state("Reset Test");

        // Test 2: Increment once
        train_action(1, 1);
        expected_state = 2'b10;
        verify_state("Increment Test1");

        // Test 3: Increment to saturation
        train_action(1, 1);
        expected_state = 2'b11;
        verify_state("Increment to Saturation Test");

        // Test 4: Remain at saturation 
        train_action(1, 1);
        verify_state("Remain at Saturation");

        // Test 5: Decrement once from saturation
        train_action(1, 0);
        expected_state = 2'b10;
        verify_state("Decrement Test");

        // Test 6: Decrement to zero
        train_action(1, 0);
        train_action(1, 0);
        expected_state = 2'b00;
        verify_state("Decrement to Zero Test");

        // Test 7: Check remain at zero
        train_action(1, 0);
        verify_state("Remain at Zero");

        // Test 8: Ensure counter holds value when train_valid is 0
        train_action(0, 1);
        train_action(0, 0);
        expected_state = 2'b00;
        verify_state("Hold Value Test");

        // Display results
        if (!test_failed) 
            $display("all test cases passed");

        $finish;
    end

    // Task for setting inputs and triggering clock edge
    task train_action;
        input valid;
        input taken;
        begin
            train_valid = valid;
            train_taken = taken;
            @(posedge clk);
        end
    endtask

    // Task for verifying the counter state against expected value
    task verify_state;
        input string testname;
        begin
            if (state !== expected_state) begin
                $display("%s failed: expected %b, got %b", testname, expected_state, state);
                test_failed = 1;
            end
        end
    endtask

endmodule

