module testbench;
    reg clk, reset, data, ack;
    wire [3:0] count;
    reg counting, done;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .reset(reset),
        .data(data),
        .count(count),
        .counting(counting),
        .done(done),
        .ack(ack)
    );

    // Clock generation
    always #5 clk = ~clk; // 100 MHz clock

    // Test vectors and response checking
    initial begin
        $monitor("At time %t count = %h counting = %b done= %b", $time, count, counting, done);
        clk = 0; 
        reset = 1; 
        data = 0; 
        ack = 0;
        #10 reset = 0; 

        // Test Case 1 - Proper initialization after reset
        @(posedge clk);
        reset = 1;
        @(posedge clk);
        reset = 0;

        // Should wait for 1101 pattern
        checkDoneCounting();

        // Send pattern 1101
        sendPattern(4'b1101);

        // Send delay[3:0] = 4
        sendData(4'b0100);

        // Expect counting to be true and wait for 5000 cycles (count down from 4 to 0)
        checkCounting(5, 4);

        // Acknowledge the timer and test reset to initial state
        ack = 1;
        @(posedge clk);
        ack = 0;

        // Test Case 2 - Different delay
        sendPattern(4'b1101);
        sendData(4'b0010);

        // Expect counting to start for 3000 clock cycles
        checkCounting(3, 2);

        // Final test completion check
        if (passedTests == 2) begin
            $display("all test cases passed");
        end
        $finish;
    end

    task sendPattern;
        input [3:0] pattern;
        integer i;
        begin
            for (i = 0; i < 4; i = i + 1) begin
                data = pattern[i];
                @(posedge clk);
            end
        end
    endtask

    task sendData;
        input [3:0] dataBits;
        integer i;
        begin
            for (i = 3; i >= 0; i = i - 1) begin
                data = dataBits[i];
                @(posedge clk);
            end
        end
    endtask

    task checkCounting;
        input integer cycles, expectedDelay;
        integer i;
        begin
            for (i = expectedDelay; i >= 0; i = i - 1) begin
                repeat (1000) @(posedge clk);
                if (count != i) begin
                    $display("Error: Expected count = %d, Got count = %d", i, count);
                    passedTests = passedTests - 1;
                    disable checkCounting;
                end
            end
        end
    endtask

    task checkDoneCounting;
        begin
            if (done !== 1'b0 || counting !== 1'b0) begin
                $display("Error: done or counting not in expected idle state!");
                passedTests = passedTests - 1;
            end
        end
    endtask

    integer passedTests = 2; // total number of tests set initially
endmodule

