        if (anyedge !== 8'b00000000) $display("Test case 1 failed.");

        // Test Scenario 2: All edges
        in = 8'b10101010;
        prev_in = 8'b01010101;
        #10;
        if (anyedge !== 8'b11111111) $display("Test case 2 failed.");

        // Test Scenario 3: Random pattern with single bit edges
        in = 8'b11010010;
        prev_in = 8'b10101010;
        #10;
        if (anyedge !== calcExpectedEdges(prev_in, in)) $display("Test case 3 failed.");

        // Test Scenario 4: Transition back to initial
        in = 8'b00000000;
        prev_in = 8'b11010010;
        #10;
        if (anyedge !== calcExpectedEdges(prev_in, in)) $display("Test case 4 failed.");

        // All tests passed
        if (1) $display("all test cases passed");

        // Finish simulation
        $finish;
    end

    // Function to calculate expected edge detection
    function [7:0] calcExpectedEdges(input [7:0] prev, input [7:0] current);
        begin
            calcExpectedEdges = (prev ^ current);
        end
    endfunction
