module tb_top_module();

  reg clk;
  reg areset;
  reg load;
  reg ena;
  reg [3:0] data;
  wire [3:0] q;

  top_module DUT (
    .clk(clk),
    .areset(areset),
    .load(load),
    .ena(ena),
    .data(data),
    .q(q)
  );

  initial begin
    clk = 0;
    forever #5 clk = ~clk; // Generate clock with 10ns period
  end

  initial begin
    areset = 1;
    load = 0;
    ena = 0;
    data = 4'b0000;

    #10;
    areset = 0; // Release reset
    #10; 
    load = 1;
    data = 4'b1010; // Load data into shift register
    #10;
    load = 0;
    #20;

    ena = 1; // Enable shifting
    #10;
    if (q !== 4'b0101) begin
      $display("Test case failed at time %t with q=%b", $time, q);
      $finish;
    end
    #10;
    if (q !== 4'b0010) begin
      $display("Test case failed at time %t with q=%b", $time, q);
      $finish;
    end
    #10;

    ena = 0;
    load = 1;
    data = 4'b1100; // Reload different data
    #10;
    load = 0;
    if (q !== 4'b1100) begin
      $display("Test case failed at time %t with q=%b", $time, q);
      $finish;
    end
    #10;

    ena = 1; // Resume shifting
    #10;
    if (q !== 4'b0110) begin
      $display("Test case failed at time %t with q=%b", $time, q);
      $finish;
    end
    #10;
    if (q !== 4'b0011) begin
      $display("Test case failed at time %t with q=%b", $time, q);
      $finish;
    end
    #10;

    areset = 1; // Assert reset
    #10; 
    areset = 0;
    #10;
    if (q !== 4'b0000) begin
      $display("Test case failed at time %t with q=%b", $time, q);
      $finish;
    end

    $display("all test cases passed");
    $finish;
  end

