module testbench;
    // Inputs
    reg clk;
    reg a;
    reg b;

    // Outputs
    wire out_assign;
    wire out_always_comb;
    wire out_always_ff;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .a(a), 
        .b(b),
        .out_assign(out_assign),
        .out_always_comb(out_always_comb),
        .out_always_ff(out_always_ff)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock with period 10ns
    end

    // Local variables to track test status
    reg test_failed = 0;

    // Initialize Inputs and monitor changes
    initial begin
        // Initialize Inputs
        a = 0;
        b = 0;

        // Wait for global reset to finish
        #100;

        // Add stimulus here: Test all combinations
        $display("Testing (0,0)...");
        a = 0; b = 0;
        #10; // Wait to account for both combinational and sequential logic
        check_output(0);

        $display("Testing (0,1)...");
        a = 0; b = 1;
        #10;
        check_output(1);

        $display("Testing (1,0)...");
        a = 1; b = 0;
        #10;
        check_output(1);

        $display("Testing (1,1)...");
        a = 1; b = 1;
        #10;
        check_output(0);

        // Check if all tests passed
        if (!test_failed) begin
            $display("all test cases passed");
        end

        // Finish the simulation
        $finish;
    end

    // Procedure to check outputs and display respective messages
    task check_output;
        input expected_output;
        begin
            if (out_assign !== expected_output || out_always_comb !== expected_output) begin
                $display("Failure for inputs (%b, %b): out_assign=%b, out_always_comb=%b, Expected=%b", 
                          a, b, out_assign, out_always_comb, expected_output);
                test_failed = 1;
            end else if (clk && (out_always_ff !== expected_output)) begin // Check only on the positive edge
                // Sequential delay, expect output to align with previous stimulus
                $display("Sequential output misaligned at (%b, %b): out_always_ff=%b, Expected=%b", 
                          a, b, out_always_ff, expected_output);
                test_failed = 1;
            end
        end
    endtask

endmodule

