    reg clk;
    reg [7:0] in;
    reg reset;

    // Output
    wire done;

    // Instantiate the DUT
    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .done(done)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz Clock
    end

    // Test cases
    initial begin
        // Initialize inputs
        reset = 1'b1;
        in = 8'd0;
        #10;
        reset = 1'b0;

        // Test Case 1: Check for three-byte sequence with correct start
        in = 8'b1000_0000; // Message start, byte 1
        #10;
        in = 8'b0111_1111; // byte 2
        #10;
        in = 8'b0011_1100; // byte 3
        #10;
        if (done !== 1'b1) $display("Test case failed.");

        // Test Case 2: Interrupted sequence before a valid start
        reset = 1'b1; #10; reset = 1'b0;
        in = 8'b0110_0000; // Invalid start
        #10;
        in = 8'b1000_0000; // Correct start after invalid one
        #10;
        in = 8'b0000_1100; // byte 2
        #10;
        in = 8'b0011_1100; // byte 3
        #10;
        if (done !== 1'b1) $display("Test case failed.");

        // Test Case 3: Correct byte followed by immediate reset
        reset = 1'b1; #10; reset = 1'b0;
        in = 8'b1000_0000; // supposed start of message
        #10;
        reset = 1'b1; #10; reset = 1'b0; // reset during the sequence
        in = 8'b1000_0000; // new start
        #10;
        in = 8'b0000_0101; // byte 2
        #10;
        in = 8'b0001_1010; // byte 3
        #10;
        if (done !== 1'b1) $display("Test case failed.");

        // Check all tests passed
        if (!$test$plusargs("Test case failed.")) $display("all test cases passed");

        // Finish simulation
        $finish;
    end

