module testbench;
    // Inputs
    reg clk;
    reg areset;
    reg bump_left;
    reg bump_right;

    // Outputs
    wire walk_left;
    wire walk_right;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .walk_left(walk_left),
        .walk_right(walk_right)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock period of 10 ns
    end

    // Variables to monitor test success
    integer tests_passed;
    initial tests_passed = 0;

    // Test Cases
    initial begin
        // Initialize Inputs
        areset = 1; bump_left = 0; bump_right = 0;
        #10; // Wait for the asynchronous reset to take effect

        areset = 0;
        // Test Case 1 - Initial state, walking left
        if (walk_left == 1'b1 && walk_right == 1'b0) tests_passed = tests_passed + 1;
        else $display("Test Case 1 Failed");

        // Test Case 2 - Bump on the left should cause walking right
        bump_left = 1; #10;
        if (walk_left == 1'b0 && walk_right == 1'b1) tests_passed = tests_passed + 1;
        else $display("Test Case 2 Failed");

        // Test Case 3 - Clear bumps, continue walking right
        bump_left = 0; #10;
        if (walk_left == 1'b0 && walk_right == 1'b1) tests_passed = tests_passed + 1;
        else $display("Test Case 3 Failed");

        // Test Case 4 - Bump on the right should cause walking left
        bump_right = 1; #10;
        if (walk_left == 1'b1 && walk_right == 1'b0) tests_passed = tests_passed + 1;
        else $display("Test Case 4 Failed");

        // Test Case 5 - Bump on both sides, should switch direction to right
        bump_left = 1; bump_right = 1; #10;
        if (walk_left == 1'b0 && walk_right == 1'b1) tests_passed = tests_passed + 1;
        else $display("Test Case 5 Failed");

        // Assess all tests passed
        if (tests_passed == 5) $display("all test cases passed");
    end

    // Testbench Cleanup
    initial begin
        #100;  // Allow sufficient time for all tests to run
        $finish;
    end
endmodule

