module testbench;
    reg clk;
    reg reset;
    wire [9:0] q;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .reset(reset),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock period of 10ns
    end

    // Stimuli: apply test cases
    initial begin
        reset = 1;
        #20;       // Wait for a couple of clock cycles with reset active
        reset = 0;

        // Check proper reset behavior
        if (q !== 10'd0) begin
            $display("Reset test failed.");
            $finish;
        end

        // Wait and observe counting behavior
        repeat(999) begin
            @(posedge clk);
            if (reset) begin
                if (q !== 10'd0) begin
                    $display("Reset during counting test failed at cycle %d.", $time/10);
                    $finish;
                end
            end else begin
                if (q !== ($time/10 - 2)) begin
                    $display("Counting test failed at cycle %d. Expected %d, found %d.", $time/10, $time/10 - 2, q);
                    $finish;
                end
            end
        end

        // Final conditional check to confirm all test passed
        @(posedge clk);
        if (q === 10'd999) begin
            @(posedge clk);
            if (q === 10'd0) begin
                $display("all test cases passed");
            end else begin
                $display("Failed to wrap at max value.");
            end
        end else begin
            $display("Final count incorrect. Expected 999, found %d.", q);
        end

        $finish;  // End simulation
    end

    // Reset randomly during counting for robustness
    always begin
        #237;       // some random time
        reset = 1'b1;
        #20;        // apply reset for some time
        reset = 1'b0;
    end

endmodule

