    top_module uut (
        .in(in), 
        .out_and(out_and), 
        .out_or(out_or), 
        .out_xor(out_xor)
    );

    // Local variables
    integer i;
    bit test_failed = 0;

    // Expected values calculation
    function automatic bit expected_and(input [3:0] in);
        expected_and = &in;
    endfunction

    function automatic bit expected_or(input [3:0] in);
        expected_or = |in;
    endfunction

    function automatic bit expected_xor(input [3:0] in);
        expected_xor = ^in;
    endfunction

    // Test stimulus
    initial begin
        // Check all possible inputs
        for (i = 0; i < 16; i = i + 1) begin
            in = i;
            #10;  // Wait for outputs to be stable

