            if (out_or !== expected_or(in)) begin
                $display("Test failed for input %b: Expected OR %b, got %b", in, expected_or(in), out_or);
                test_failed = 1;
            end
            if (out_xor !== expected_xor(in)) begin
                $display("Test failed for input %b: Expected XOR %b, got %b", in, expected_xor(in), out_xor);
                test_failed = 1;
            end
        end

        // Check if all tests passed
